    <script>
        document.addEventListener("DOMContentLoaded", function () {
    // Initialize Swiper
    const swiper = new Swiper('.mySwiper', {
        loop: true,
        autoplay: {
            delay: 3000,
            disableOnInteraction: false,
        },
        pagination: {
            el: '.swiper-pagination',
            clickable: true,
        },
        navigation: {
            nextEl: '.swiper-button-next',
            prevEl: '.swiper-button-prev',
        },
    });

    // Select all icon-box elements
    const iconBoxes = document.querySelectorAll('.icon-box');

    // Function to handle slide events and update active class
    function handleSlideEvent(index) {
        // Remove active class from all icon-boxes and paths
        iconBoxes.forEach((box, i) => {
            const path = box.querySelector('.icon-wrapper path');
            const iconWrapper = box.querySelector('.icon-wrapper');

            // Remove active class from path and icon-box
            if (path) path.classList.remove('active');
            if (iconWrapper) iconWrapper.classList.remove('active');
        });

        // Add active class to the corresponding icon-box and path
        const activeBox = iconBoxes[index];
        if (activeBox) {
            const activePath = activeBox.querySelector('.icon-wrapper path');
            const activeWrapper = activeBox.querySelector('.icon-wrapper');

            if (activePath) activePath.classList.add('active');
            if (activeWrapper) activeWrapper.classList.add('active');
        }
    }

    // Listen for slide changes and call the function for each slide
    swiper.on('slideChange', function () {
        const activeIndex = swiper.realIndex; // Get the current slide index
        handleSlideEvent(activeIndex); // Trigger event based on slide index
    });

    // Initialize with the first icon active
    handleSlideEvent(swiper.realIndex);


});

    // This script dynamically updates the source of partner images based on screen size.

    document.addEventListener("DOMContentLoaded", function () {
        const images = document.querySelectorAll('.partner .image img'); // اختر جميع الصور داخل الشركاء
        const alternativeImages = [
            "{{ asset('assets/images/site/partners/1s.png') }}",
            "{{ asset('assets/images/site/partners/2s.png') }}",
            "{{ asset('assets/images/site/partners/3s.png') }}",
            "{{ asset('assets/images/site/partners/4s.png') }}",
            "{{ asset('assets/images/site/partners/5s.png') }}"
        ]; // روابط الصور البديلة

        const defaultImages = [
            "{{ asset('assets/images/site/partners/1.png') }}",
            "{{ asset('assets/images/site/partners/2.png') }}",
            "{{ asset('assets/images/site/partners/3.png') }}",
            "{{ asset('assets/images/site/partners/4.png') }}",
            "{{ asset('assets/images/site/partners/5.png') }}"
        ]; // روابط الصور الافتراضية

        function updateImages() {
            images.forEach((img, index) => {
                if (window.innerWidth <= 768) {
                    img.src = alternativeImages[index]; // استخدم الصور البديلة للشاشات الصغيرة
                } else {
                    img.src = defaultImages[index]; // استخدم الصور الافتراضية للشاشات الكبيرة
                }
            });
        }

        // استدعاء الدالة عند تحميل الصفحة وتغيير حجم الشاشة
        updateImages();
        window.addEventListener('resize', updateImages);
    });

    </script>
